pd_idx_t tangle_faces[4] = {0,3,5,1} ;
pd_idx_t tangle_edges[4] = {6,2,12,14} ;
pd_boundary_or_t edge_bdy_or[4] = {in,out,out,in} ;

pd_idx_t ninterior_cross = 5 ;
